def get_messier_catalog() -> list[CatalogObject]:
    """Return the complete Messier catalog."""
    return MESSIER_CATALOG


def get_messier_by_number(number: int) -> CatalogObject | None:
    """Get a specific Messier object by number (1-110)."""
    if 1 <= number <= 110:
        return MESSIER_CATALOG[number - 1]
    return None
